const results = $items().map(item => {
    console.log('Structure Output - Processing item:', JSON.stringify(item, null, 2));
const aiAgentOutput = $input.first().json.output;
    try {
        let aiAgentOutput;

        // The AI Agent with Structured Output Parser returns data directly in the json object
        // Check if data is already parsed (from Structured Output Parser)
        if (item.json.tool_name && item.json.mcp_raw_response) {
            // Data is already properly structured
            aiAgentOutput = item.json;
            console.log('AI Agent - Data already structured:', aiAgentOutput);
        }
        // Check for 'output' field (text response that needs parsing)
        else if (item.json.output) {
            const textContent = item.json.output;
            console.log('AI Agent output field (text):', textContent);

            try {
                // Try to parse JSON from text content
                const jsonMatch = textContent.match(/```(?:json)?\s*([\s\S]*?)```/);
                const jsonString = jsonMatch ? jsonMatch[1].trim() : textContent.trim();
                aiAgentOutput = JSON.parse(jsonString);
            } catch (parseError) {
                console.log('JSON parse error, attempting to fix:', parseError.message);
                console.log('Failed text:', textContent.substring(0, 500));

                // Remove code blocks if present
                let cleanText = textContent.replace(/```(?:json)?\s*|```/g, '').trim();

                // Replace escaped newlines with actual spaces for better parsing
                cleanText = cleanText.replace(/\\n/g, ' ');

                // Remove extra whitespace
                cleanText = cleanText.replace(/\s+/g, ' ');

                // Fix common AI Agent issue: stray ] after mcp_raw_response closes
                // Pattern: }}}] followed by comma and next field (should be }}}, )
                cleanText = cleanText.replace(/(\}\}\})\s*\]\s*,\s*/g, '$1, ');
                console.log('Removed stray array brackets after mcp_raw_response');

                // Fix common AI Agent issue: duplicate node data after mcp_raw_response
                // Find where the standard fields end and truncate any extra data
                const standardFieldsPattern = /("needs_approval"\s*:\s*(?:true|false)\s*,\s*"issue_detected"\s*:\s*(?:true|false)\s*,\s*"applied_fix"\s*:\s*(?:true|false))\s*,\s*"[A-Z]/;
                const match = cleanText.match(standardFieldsPattern);
                if (match) {
                    // Found extra data after standard fields - truncate it
                    const truncateIndex = match.index + match[1].length;
                    cleanText = cleanText.substring(0, truncateIndex) + '}';
                    console.log('Truncated duplicate data after standard fields');
                }

                console.log('Cleaned text:', cleanText.substring(0, 500));

                // The AI Agent outputs incomplete JSON - missing closing brace
                // Count braces to see if we need to close the JSON
                const openBraces = (cleanText.match(/\{/g) || []).length;
                const closeBraces = (cleanText.match(/\}/g) || []).length;
                console.log(`Brace count - open: ${openBraces}, close: ${closeBraces}`);

                if (openBraces > closeBraces) {
                    const missingBraces = openBraces - closeBraces;
                    cleanText += '}'.repeat(missingBraces);
                    console.log(`Added ${missingBraces} closing brace(s)`);
                }

                console.log('Attempting to parse fixed JSON');
                try {
                    aiAgentOutput = JSON.parse(cleanText);
                    console.log('Successfully parsed after cleaning and fixing');
                } catch (secondError) {
                    console.error('Still unable to parse:', secondError.message);
                    console.log('Attempting manual extraction...');

                    // Manual extraction as last resort
                    const toolNameMatch = cleanText.match(/"tool_name"\s*:\s*"([^"]+)"/);
                    const targetNodeMatch = cleanText.match(/"target_node"\s*:\s*"([^"]+)"/);
                    const mcpToolsMatch = cleanText.match(/"mcp_tools_used"\s*:\s*\[([^\]]+)\]/);
                    const needsApprovalMatch = cleanText.match(/"needs_approval"\s*:\s*(true|false)/);
                    const issueDetectedMatch = cleanText.match(/"issue_detected"\s*:\s*(true|false)/);
                    const appliedFixMatch = cleanText.match(/"applied_fix"\s*:\s*(true|false)/);

                    // Extract mcp_raw_response - find the entire nested object
                    let mcpRawResponse = null;
                    const mcpStart = cleanText.indexOf('"mcp_raw_response"');
                    if (mcpStart !== -1) {
                        const colonIndex = cleanText.indexOf(':', mcpStart);
                        let braceCount = 0;
                        let startIndex = -1;
                        let endIndex = -1;

                        for (let i = colonIndex; i < cleanText.length; i++) {
                            if (cleanText[i] === '{') {
                                if (startIndex === -1) startIndex = i;
                                braceCount++;
                            } else if (cleanText[i] === '}') {
                                braceCount--;
                                if (braceCount === 0) {
                                    endIndex = i + 1;
                                    break;
                                }
                            }
                        }

                        if (startIndex !== -1 && endIndex !== -1) {
                            const mcpString = cleanText.substring(startIndex, endIndex);
                            try {
                                mcpRawResponse = JSON.parse(mcpString);
                                console.log('Successfully extracted mcp_raw_response');
                            } catch (e) {
                                console.error('Failed to parse extracted mcp_raw_response:', e.message);
                            }
                        }
                    }

                    if (toolNameMatch && mcpRawResponse) {
                        aiAgentOutput = {
                            tool_name: toolNameMatch[1],
                            target_node: targetNodeMatch ? targetNodeMatch[1] : 'all',
                            mcp_tools_used: mcpToolsMatch ? JSON.parse('[' + mcpToolsMatch[1] + ']') : [],
                            mcp_raw_response: mcpRawResponse,
                            needs_approval: needsApprovalMatch ? needsApprovalMatch[1] === 'true' : false,
                            issue_detected: issueDetectedMatch ? issueDetectedMatch[1] === 'true' : false,
                            applied_fix: appliedFixMatch ? appliedFixMatch[1] === 'true' : false
                        };
                        console.log('Manually reconstructed JSON');
                    } else {
                        throw secondError;
                    }
                }
            }
        }
        // Fallback - try to use item.json directly
        else {
            aiAgentOutput = item.json;
        }

        console.log('Parsed AI Agent Output:', JSON.stringify(aiAgentOutput, null, 2));

        // Extract metadata from AI response
        const toolName = aiAgentOutput.tool_name || 'unknown';
        const targetNode = aiAgentOutput.target_node || 'all';
        const mcpToolsUsed = aiAgentOutput.mcp_tools_used || [];
        const needsApproval = aiAgentOutput.needs_approval || false;
        const issueDetected = aiAgentOutput.issue_detected || false;
        const appliedFix = aiAgentOutput.applied_fix || false;

        // Parse MCP raw response
        let mcpRaw = aiAgentOutput.mcp_raw_response || {};
        console.log('Initial mcp_raw_response:', JSON.stringify(mcpRaw, null, 2));

        if (typeof mcpRaw === 'string') {
            mcpRaw = JSON.parse(mcpRaw);
        }

        // Extract actual response data from nested Tool structure
        // The structure is: Tool.response[0].text which contains the actual node data
        if (mcpRaw.Tool && mcpRaw.Tool.response && Array.isArray(mcpRaw.Tool.response)) {
            console.log('Extracting from Tool.response array');
            if (mcpRaw.Tool.response.length > 0 && mcpRaw.Tool.response[0].text) {
                console.log('Found text field in response[0]');
                mcpRaw = mcpRaw.Tool.response[0].text;
            } else {
                mcpRaw = mcpRaw.Tool.response;
            }
        }

        console.log('Processed mcp_raw_response:', JSON.stringify(mcpRaw, null, 2));

        // === FORMAT OUTPUT BASED ON MCP DATA ===
        let final_message = '';
        let monitoringResults = {};
        let queryType = 'generic';

        // GET NODE STATUS - Enhanced formatting for node status display
        if (toolName === 'get_node_status') {
            let nodes = [];

            // mcpRaw has already been processed and contains the actual node data
            // Check if it's a direct array
            if (Array.isArray(mcpRaw)) {
                console.log('Using direct array format');
                nodes = mcpRaw.map(node => ({
                    ...node,
                    // Use the name from the node data or fall back to host
                    name: node.name || node.host
                }));
            }
            // Handle object with node names as keys (most common format from MCP server)
            else if (typeof mcpRaw === 'object' && !Array.isArray(mcpRaw)) {
                console.log('Using object format with node names as keys');
                nodes = Object.entries(mcpRaw).map(([name, data]) => ({ 
                    name: name.replace(/^(PVE|PBS)-/, ''), 
                    ...data 
                }));
            }

            console.log('Processed nodes:', JSON.stringify(nodes, null, 2));

            if (nodes.length > 0) {
                monitoringResults = {
                    nodes_online: nodes.filter(node => node.status === 'online').length,
                    nodes_total: nodes.length
                };
                queryType = 'node_status';

                // Check if all nodes are online
                const allOnline = nodes.every(node => node.status === 'online');
                const statusHeader = allOnline ? '‚úÖ All Systems Operational' : '‚ö†Ô∏è Some Systems Offline';

                // Build the header
                final_message = `${statusHeader}\nüîß GET NODE STATUS : ${targetNode.toUpperCase()}\n\n`;

                // Format each node
                nodes.forEach((nodeData) => {
                    const statusIcon = nodeData.status === 'online' ? "üü¢" : "üî¥";

                    final_message += `${nodeData.name}: ${statusIcon}\n`;
                    final_message += `Type: ${nodeData.type}\n`;
                    final_message += `IP: ${nodeData.host}\n\n`;
                });
            } else {
                final_message = `üîß **GET NODE STATUS**\n\nNo node data available.`;
            }
        }
        // NODE HEALTH CHECK
        else if (toolName === 'check_node_health' && mcpRaw.connectivity_test) {
            const ct = mcpRaw.connectivity_test;
            monitoringResults = {
                nodes_online: ct.nodes_healthy || 0,
                nodes_total: ct.nodes_checked || 0
            };
            queryType = 'health_check';

            const statusIcon = (ct.nodes_healthy === ct.nodes_checked) ? "üü¢" : "üî¥";
            final_message = `üñ•Ô∏è **Proxmox Infrastructure Status**\n\n${statusIcon} **Nodes:** ${ct.nodes_healthy}/${ct.nodes_checked} Online\n\n`;

            if (ct.detailed_connectivity) {
                final_message += `üìä **Node Details:**\n`;
                ct.detailed_connectivity.forEach(n => {
                    const icon = n.success ? "üü¢" : "üî¥";
                    final_message += `${icon} **${n.node_name}** (${n.type}) - ${n.host} - v${n.version}\n`;
                });
            }

            const health = mcpRaw.overall_health || 'unknown';
            final_message += `\n‚úÖ **Overall Health:** ${health.toUpperCase()}`;
        }
        // VM LIST - when mcp_raw_response is already an array (not nested)
        else if (Array.isArray(aiAgentOutput.mcp_raw_response)) {
            const vms = aiAgentOutput.mcp_raw_response;
            if (vms.some(v => v.vmid)) {
                monitoringResults = {
                    total_vms: vms.length,
                    vms_running: vms.filter(v => v.status === 'running').length
                };
                queryType = 'vm_list';

                final_message = `üíª **Virtual Machines on ${targetNode}**\n\nÔøΩ **Summary:** ${vms.length} VMs (${monitoringResults.vms_running} running)\n\n`;

                vms.forEach(vm => {
                    const icon = vm.status === 'running' ? "üü¢" : "üî¥";
                    final_message += `${icon} **${vm.name || 'VM-' + vm.vmid}** (ID: ${vm.vmid})\n`;
                    if (vm.status === 'running') {
                        const memGB = ((vm.mem || 0) / 1024 ** 3).toFixed(1);
                        const maxGB = ((vm.maxmem || 0) / 1024 ** 3).toFixed(1);
                        final_message += `   ‚Ä¢ Memory: ${memGB}/${maxGB} GB ‚Ä¢ CPU: ${vm.cpus || '?'} cores\n`;
                    }
                });
            }
        }
        // FALLBACK
        else {
            final_message = `üîß **${toolName.replace(/_/g, ' ').toUpperCase()}**\n\nNode: ${targetNode}\n\nData received successfully from MCP server.`;
        }

        const result = {
            json: {
                final_message,
                combinedMessage: final_message,
                target_node: targetNode,
                mcp_tools_used: mcpToolsUsed,
                mcp_raw_response: aiAgentOutput.mcp_raw_response,
                monitoring_results: monitoringResults,
                details: { query_type: queryType, priority: 'normal', tool_name: toolName },
                issue_detected: issueDetected,
                applied_fix: appliedFix,
                needs_approval: needsApproval,
                commands_requested: needsApproval ? `${toolName} on ${targetNode}` : null,
                branch: needsApproval ? "send_and_wait" : "notify_user",
                subworkflow: "proxmox_mcp",
                status_summary: final_message,
                raw_response: aiAgentOutput
            }
        };

        console.log('Structure Output - Result:', JSON.stringify(result, null, 2));
        return result;

    } catch (error) {
        console.error('Structure Output - Error:', error);
        return {
            json: {
                final_message: `‚ùå **Error:** ${error.message}`,
                combinedMessage: `Error: ${error.message}`,
                target_node: "unknown",
                mcp_tools_used: [],
                monitoring_results: {},
                details: { query_type: "error", priority: "high", error: error.message },
                issue_detected: true,
                applied_fix: false,
                needs_approval: false,
                commands_requested: null,
                branch: "notify_user",
                subworkflow: "error",
                status_summary: `Error: ${error.message}`,
                raw_response: item.json
            }
        };
    }
});

return results;

try {
  const userReply = ($json.content || $json.user_input || "").toLowerCase();
  let approved = false;

  if (userReply === "approve") {
    approved = true;
    $json.AIAgentOutput.needs_approval = false;
  } else if (userReply === "deny") {
    approved = false;
    $json.AIAgentOutput.needs_approval = false;
  } else {
    // Invalid input
    $json.AIAgentOutput.needs_approval = true;
    $json.AIAgentOutput.applied_fix = false;
    $json.AIAgentOutput.details = $json.AIAgentOutput.details || {};
    $json.AIAgentOutput.details.invalid_input = $json.content || $json.user_input;
    $json.AIAgentOutput.message = 'Approval input invalid or missing';
  }

  return [{
    json: {
      approval: approved,
      target_node: $json.target_node,
      url: $json.url,
      response_type: $json.response_type,
      details: $json.details,
      node_auth: $json.node_auth,
      combinedMessage: $json.combinedMessage,
      needs_approval: $json.AIAgentOutput.needs_approval,
      applied_fix: $json.AIAgentOutput.applied_fix,
      message: $json.AIAgentOutput.message
    }
  }];
} catch(err) {
  $json.AIAgentOutput.issue_detected = true;
  $json.AIAgentOutput.details = $json.AIAgentOutput.details || {};
  $json.AIAgentOutput.details.error = err.message;

  return [{
    json: {
      approval: false,
      target_node: $json.target_node,
      url: $json.url,
      response_type: $json.response_type,
      details: $json.details,
      node_auth: $json.node_auth,
      combinedMessage: $json.combinedMessage,
      needs_approval: true,
      applied_fix: false,
      message: 'Error processing approval response'
    }
  }];
}

