={{ 
  $items().map(item => {
    const json = item.json;
    let statusEmoji = "‚ö™";
    let statusText = "";

    if (json.needs_approval && json.approval === false) {
      statusEmoji = "üü°"; statusText = "Action declined by user";
    } else if (json.executed && json.response?.error) {
      statusEmoji = "üî¥"; statusText = `Error: ${json.response.error}`;
    } else if (json.executed) {
      statusEmoji = "üü¢"; statusText = "Action executed successfully";
    } else if (!json.executed && json.needs_approval) {
      statusEmoji = "üü†"; statusText = "Action skipped ‚Äî awaiting approval";
    } else {
      statusEmoji = "üîµ"; statusText = "Action not executed";
    }

    return `${statusEmoji} Node: ${json.target_node}\nAction: ${json.response_type} ${json.url}\nStatus: ${statusText}\n${json.combinedMessage || ""}`;
  }).join("\n----------------------------------------\n")
}}

parameters.jsCode:
const items = $input.all();

return items.map(item => {
    const data = item.json;
    const response = data.mcp_raw_response || data.data || {}; // USE MCP RAW RESPONSE
    const subworkflow = $('When Executed by Another Workflow').first().json.SubWorkflow;
    const structure = $('Structure Output').first().json || {};

    const needs_approval = structure.needs_approval;
    const issue_detected = structure.issue_detected ?? false;
    const applied_fix = structure.applied_fix ?? false;
    const target_node = structure.target_node || "unknown";
    const details = structure.details || {};
    const queryType = details.query_type || details.query || "generic";
    const monitoringResults = structure.monitoring_results || {};
    const mcpToolsUsed = structure.mcp_tools_used || [];
    const mcpRawResponse = structure.mcp_raw_response || {}; // ACCESS RAW MCP DATA

    let message = "";
    let summary = "";

    // === Declare common variables at function scope ===
    const criticalIssues = monitoringResults.critical_issues || 0;
    const warnings = monitoringResults.warnings || 0;

    // === COMPREHENSIVE INFRASTRUCTURE STATUS ===
    if (queryType === "health_check" || queryType === "comprehensive_health_check") {
        const nodesOnline = monitoringResults.nodes_online || monitoringResults.nodes_checked?.length || 0;
        const vmsRunning = monitoringResults.vms_running || 0;
        const lxcsRunning = monitoringResults.lxcs_running || 0;

        summary = `üèóÔ∏è **Infrastructure Health Report**\n`;
        summary += `üìä **Overview:**\n`;
        summary += `- Nodes Online: ${nodesOnline} üü¢\n`;
        summary += `- VMs Running: ${vmsRunning} üíª\n`;
        summary += `- LXCs Running: ${lxcsRunning} üì¶\n`;

        // ADD DETAILED NODE INFORMATION FROM MCP RAW RESPONSE
        if (mcpRawResponse.connectivity_test?.detailed_connectivity) {
            summary += `\nüñ•Ô∏è **Node Details:**\n`;
            mcpRawResponse.connectivity_test.detailed_connectivity.forEach(node => {
                const statusIcon = node.success ? 'üü¢' : 'üî¥';
                summary += `${statusIcon} **${node.node_name}** (${node.type})\n`;
                summary += `   - Host: ${node.host}\n`;
                summary += `   - Version: ${node.version}\n`;
            });
        }

        if (criticalIssues > 0) {
            summary += `- Critical Issues: ${criticalIssues} üî¥\n`;
        }
        if (warnings > 0) {
            summary += `- Warnings: ${warnings} üü°\n`;
        }

        if (mcpToolsUsed.length > 0) {
            summary += `\nüîß **MCP Tools Used:** ${mcpToolsUsed.join(', ')}\n`;
        }

        if (monitoringResults.performance_summary) {
            summary += `\nüìà **Performance Summary:**\n`;
            Object.entries(monitoringResults.performance_summary).forEach(([key, value]) => {
                summary += `- ${key}: ${value}\n`;
            });
        }
    }

    // === AUTOMATED MAINTENANCE RESULTS ===
    else if (queryType === "automated_maintenance") {
        summary = `üîß **Automated Maintenance Report**\n`;
        if (monitoringResults.issue) {
            summary += `‚ö†Ô∏è **Issue Detected:** ${monitoringResults.issue}\n`;
        }
        if (monitoringResults.fix_applied) {
            summary += `‚úÖ **Fix Applied:** ${monitoringResults.fix_applied}\n`;
        }
        if (monitoringResults.status_after_fix) {
            summary += `üìä **Status:** ${monitoringResults.status_after_fix}\n`;
        }
    }

    // === CRITICAL INCIDENT RESPONSE ===
    else if (queryType === "critical_incident") {
        summary = `üö® **Critical Incident Report**\n`;
        if (monitoringResults.critical_error) {
            summary += `‚ùå **Error:** ${monitoringResults.critical_error}\n`;
        }
        if (monitoringResults.affected_vms) {
            summary += `üñ•Ô∏è **Affected VMs:** ${monitoringResults.affected_vms.join(', ')}\n`;
        }
        if (monitoringResults.recommended_action) {
            summary += `üõ†Ô∏è **Recommended Action:** ${monitoringResults.recommended_action}\n`;
        }
        if (details.estimated_downtime) {
            summary += `‚è±Ô∏è **Estimated Downtime:** ${details.estimated_downtime}\n`;
        }
    }

    // === VM LIST HANDLER (Legacy Support) ===
    else if (Array.isArray(response) && response.some(vm => vm.vmid)) {
        const vmSummaries = response.map(vm => {
            const name = vm.name || `VM ${vm.vmid}`;
            const statusIcon = vm.status === "running" ? "üü¢" : "üî¥";
            const usedMemGB = (vm.mem / 1024 ** 3).toFixed(2);
            const totalMemGB = (vm.maxmem / 1024 ** 3).toFixed(2);
            const uptimeHours = (vm.uptime / 3600).toFixed(1);

            return `${statusIcon} **${name}** (VMID: ${vm.vmid})\n- Status: ${vm.status}\n- CPU Cores: ${vm.cpus}\n- Memory: ${usedMemGB} GB / ${totalMemGB} GB\n- Uptime: ${uptimeHours} hours\n- Tags: ${vm.tags || "None"}`;
        }).join("\n\n");

        summary = `üíª **Virtual Machine Overview for ${target_node}**\n\n${vmSummaries}`;
    }

    // === STATUS SUMMARY FALLBACK ===
    else if (structure.status_summary) {
        summary = structure.status_summary;
    }

    // === Logic-based notification ===
    if (!needs_approval) {
        if (!issue_detected) {
            if (criticalIssues === 0 && warnings === 0) {
                message = `‚úÖ **All Systems Operational**\n${summary}`;
            } else {
                message = `‚ÑπÔ∏è **Status Update**\n${summary}`;
            }
        } else if (applied_fix) {
            message = `üõ†Ô∏è **Issue Auto-Resolved**\n${summary}`;
        } else {
            message = `‚ö†Ô∏è **Issue Detected**\n${summary}`;
        }
    } else {
        const priority = details.priority === "critical" ? "üö®" : "‚ö†Ô∏è";
        message = `${priority} **Action Requires Approval**\n${summary}`;
    }

    // === Return structured output for parent workflow ===
    return {
        json: {
            ...data,
            subworkflow,
            summary,
            final_message: message,
            raw_response: response,
            mcp_raw_response: mcpRawResponse, // INCLUDE MCP RAW DATA
            metadata: {
                query_type: queryType,
                needs_approval,
                issue_detected,
                applied_fix,
                target_node,
                subworkflow,
                monitoring_results: monitoringResults,
                mcp_tools_used: mcpToolsUsed,
                mcp_raw_response: mcpRawResponse // ALSO IN METADATA
            }
        }
    };
});

// Handle approved MCP actions execution results
const results = [];
let humanReadableSummary = "";

for (const item of $items()) {
    const {
        target_node,
        needs_approval,
        approval,
        combinedMessage,
        commands_requested,
        mcp_tools_used,
        monitoring_results
    } = item.json;

    let executed = false;
    let response = null;
    let actionTaken = false;

    // Determine if we should execute:
    const canExecute = !needs_approval || (needs_approval && approval === true);

    if (canExecute) {
        // Execute through MCP Server
        response = {
            success: true,
            action: commands_requested || "Health check completed",
            target_node: target_node,
            tools_used: mcp_tools_used || [],
            monitoring_results: monitoring_results || {},
            execution_time: new Date().toISOString(),
            message: "Action executed successfully via MCP Server"
        };
        executed = true;
        actionTaken = true;
    } else {
        response = { skipped: true, reason: "Approval required or denied" };
    }

    // Construct human-readable summary per item
    const actionDesc = commands_requested || "Monitoring check";
    humanReadableSummary += `\nNode: ${target_node}\nAction: ${actionDesc}\nStatus: ${executed ? "‚úÖ Executed via MCP" : "‚ùå Skipped"}\n`;

    if (response.error) {
        humanReadableSummary += `Error: ${response.error}\n`;
    } else if (response.skipped) {
        humanReadableSummary += `Reason: ${response.reason}\n`;
    } else if (executed && response.tools_used) {
        humanReadableSummary += `MCP Tools: ${response.tools_used.join(', ')}\n`;
    }

    humanReadableSummary += "----------------------------------------";

    results.push({
        json: {
            ...item.json,
            executed,
            response,
            actionTaken,
            humanReadableSummary
        }
    });
}

return results;

