        humanReadableSummary += `MCP Tools: ${(mcp_tools_used || []).join(', ')}\n`;
        humanReadableSummary += `Message: ${combinedMessage}\n----------------------------------------`;

        results.push({
            json: {
                ...item.json,
                executed: false,
                response: { 
                    skipped: true, 
                    reason: "Declined by user",
                    action_requested: commands_requested,
                    mcp_tools_planned: mcp_tools_used
                },
                actionTaken: false,
                humanReadableSummary
            }
        });
