try {
    // Safely grab JSON input
    const inputData = $input.first().json || {};
    const data = $json || {};

    // Pull known structured fields
    const metadata = inputData.metadata || {};
    const response = inputData.raw_response || inputData.data || {};
    const needs_approval = metadata.needs_approval ?? false;
    const target_node = metadata.target_node || "unknown";
    const queryType = metadata.query || "generic";

    let final_message = $('Notify User').first().json.final_message || "";
    let summary = $('Notify User').first().json.combinedMessage || "";

    // === Detect if this is a VM list ===
    if (Array.isArray(response) && response.some(vm => vm.vmid)) {
        const vmSummaries = response.map(vm => {
            const name = vm.name || `VM ${vm.vmid}`;
            const statusIcon = vm.status === "running" ? "üü¢" : "üî¥";
            const usedMemGB = vm.mem ? (vm.mem / 1024 ** 3).toFixed(2) : "0.00";
            const totalMemGB = vm.maxmem ? (vm.maxmem / 1024 ** 3).toFixed(2) : "0.00";
            const uptimeHours = vm.uptime ? (vm.uptime / 3600).toFixed(1) : "0";

            return `${statusIcon} **${name}** (VMID: ${vm.vmid})
- Status: ${vm.status}
- CPU Cores: ${vm.cpus ?? "?"}
- Memory: ${usedMemGB} GB / ${totalMemGB} GB
- Uptime: ${uptimeHours} hours
- Tags: ${vm.tags || "None"}  
`;
        }).join("\n");

        summary = `üíª **Virtual Machine Overview for ${target_node}**\n\n${vmSummaries}`;
        final_message = summary; // override with VM summary if present
    }

    // === If it‚Äôs not VM data but has an existing message ===
    else if (!final_message && inputData.summary) {
        final_message = inputData.summary;
    }

    // === Determine branch ===
    const branch = needs_approval ? 'send_and_wait' : 'notify_user';

    // === Return unified JSON ===
    return [{
        json: {
            final_message,
            summary,
            target_node,
            queryType,
            needs_approval,
            branch
        }
    }];
} catch (err) {
    return [{
        json: {
            summary: `‚ùå Error in Notify Task Complete IF SubWorkflow: ${err.message}`,
            target_node: 'unknown',
            branch: 'notify_user',
            issue_detected: true,
            applied_fix: false,
            needs_approval: false
        }
    }];
}

